mapper.select:

select * from (select * from (select to_char(orderdate, 'yyyy/mm/dd hh24:mi') orderdate, 
o.orderno, status, m.username, m.tel, arr, arrtel, request from orderTbl o join member m on o.userid=m.userid
where 1=1

and to_char(orderdate, 'yyyy/mm/dd') between #{orderSearch_from} and #{orderSearch_to}
and ${searchKey} like '%${searchWord}%'
and status like #{status}
order by orderno desc)
where rownum <= #{pageNum} * #{onePageRecord} order by orderno asc)
where rownum <= #{lastPageRecord}
#{onePageRecord} order by orderno desc

select count(orderno) as totalRecord from orderTbl
where 1=1
and to_char(orderdate, 'yyyy-mm-dd') between #{orderSearch_from} and #{orderSearch_to}
and ${searchKey} like '%${searchWord}%'
and status like #{status}
